import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';

function LoginForm() {
    const [username, setUsernamel] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [data, setData] = useState([]);

    const handleUserNameChange = (event) => {
        setUsernamel(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
    };

    function useLoginEffect(token) {
        useEffect(() => {
            localStorage.setItem('token', token);
        }, [token]);
    }

    const handleLoginSubmit = async (event) => {
        event.preventDefault();

        useEffect(() => {
            axios
                .get('/api/my-data')
                .then((response) => {
                    useLoginEffect(setData(response.data));
                    setIsLoggedIn(true);
                })
                .catch((error) => {
                    console.log(error);
                });
        }, []);
    };

    if (isLoggedIn) {
        return <Navigate to="/" />;
    }

    return (
        <div className="container mt-5">
            <h1>Login</h1>
            <form onSubmit={handleLoginSubmit}>
                <div className="mb-3">
                    <label htmlFor="username" className="form-label">
                        Username
                    </label>
                    <input type="Text" className="form-control" id="UserName" onChange={handleUserNameChange} />
                </div>
                <div className="mb-3">
                    <label htmlFor="password" className="form-label">
                        Password
                    </label>
                    <input type="password" className="form-control" id="password" onChange={handlePasswordChange} />
                </div>
                <button type="submit" className="btn btn-primary">
                    Submit
                </button>
            </form>
            {errorMessage && <div className="alert alert-danger mt-3">{errorMessage}</div>}
        </div>
    );
}

export default LoginForm;
