import React, { useState } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';
import {
    MDBBtn,
    MDBContainer,
    MDBCard,
    MDBCardBody,
    MDBCardImage,
    MDBRow,
    MDBCol,
    MDBIcon,
    MDBInput,
} from 'mdb-react-ui-kit';

function LoginForm() {
    const [username, setUsernamel] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    const handleUserNameChange = (event) => {
        setUsernamel(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        await axios
            .post('http://localhost:8000/auth/login', {
                username: username,
                password: password,
            })
            .then((response) => {
                const token = response.data.token;
                console.log(token);
                localStorage.setItem('token', token); // Lưu token vào local storage
                setIsLoggedIn(true);
            })
            .catch((error) => {
                setErrorMessage(error.response.data.message);
            });
    };

    if (isLoggedIn) {
        return <Navigate to="/" />;
    }

    return (
        <MDBContainer fluid>
            <MDBRow>
                <MDBCol sm="6">
                    <div className="d-flex flex-row ps-5 pt-5">
                        <MDBIcon fas icon="crow fa-3x me-3" style={{ color: '#709085' }} />
                        <span className="h1 fw-bold mb-0">Logo</span>
                    </div>

                    <div className="d-flex flex-column justify-content-center h-custom-2 w-75 pt-4">
                        <h3 className="fw-normal mb-3 ps-5 pb-3" style={{ letterSpacing: '1px' }}>
                            Log in
                        </h3>

                        <MDBInput
                            wrapperClass="mb-4 mx-5 w-100"
                            label="Email address"
                            id="formControlLg"
                            type="email"
                            size="lg"
                        />
                        <MDBInput
                            wrapperClass="mb-4 mx-5 w-100"
                            label="Password"
                            id="formControlLg"
                            type="password"
                            size="lg"
                        />

                        <MDBBtn className="mb-4 px-5 mx-5 w-100" color="info" size="lg">
                            Login
                        </MDBBtn>
                        <p className="small mb-5 pb-lg-3 ms-5">
                            <a class="text-muted" href="#!">
                                Forgot password?
                            </a>
                        </p>
                        <p className="ms-5">
                            Don't have an account?{' '}
                            <a href="#!" class="link-info">
                                Register here
                            </a>
                        </p>
                    </div>
                </MDBCol>

                <MDBCol sm="6" className="d-none d-sm-block px-0">
                    <img
                        src="https://ae01.alicdn.com/kf/HTB1KuOdSFXXXXa_XpXXq6xXFXXXR/UNN-Hot-Light-weight-Running-Shoes-White-Men-Sport-Shoes-SMART-CHIP-Mens-Black-Sneakers-Breathable.jpg"
                        alt="Login image"
                        className="w-100"
                        style={{ objectFit: 'cover', objectPosition: 'left' }}
                    />
                </MDBCol>
            </MDBRow>
        </MDBContainer>
    );
}

export default LoginForm;
